import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import axios from 'axios';
import './Chat.css';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [error, setError] = useState(null);
  const [showInviteModal, setShowInviteModal] = useState(false);
  const [inviteEmail, setInviteEmail] = useState('');
  const socketRef = useRef();
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Initialize socket connection
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    // Connect to WebSocket server
    socketRef.current = io('http://localhost:5001', {
      withCredentials: true,
      extraHeaders: {
        Authorization: `Bearer ${token}`
      }
    });

    // Authenticate socket connection
    socketRef.current.emit('authenticate', token);

    // Listen for new messages
    socketRef.current.on('new_message', (message) => {
      setMessages(prev => [...prev, message]);
    });

    // Listen for errors
    socketRef.current.on('error', (error) => {
      setError(error.message);
    });

    // Fetch users and messages
    const fetchData = async () => {
      try {
        // Fetch users
        const usersResponse = await axios.get('/auth/users');
        setUsers(usersResponse.data);

        // If there are users, select the first one
        if (usersResponse.data.length > 0) {
          setSelectedUser(usersResponse.data[0]);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Failed to load chat data');
      }
    };

    fetchData();

    return () => {
      socketRef.current?.disconnect();
    };
  }, [navigate]);

  // Fetch messages when selected user changes
  useEffect(() => {
    const fetchMessages = async () => {
      if (!selectedUser) return;

      try {
        const response = await axios.get(`/messages/${selectedUser._id}`);
        setMessages(response.data);
      } catch (error) {
        console.error('Error fetching messages:', error);
        setError('Failed to load messages');
      }
    };

    fetchMessages();
  }, [selectedUser]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedUser) return;

    try {
      const messageData = {
        receiverId: selectedUser._id,
        content: newMessage.trim()
      };

      // Send message through WebSocket
      socketRef.current.emit('send_message', messageData);
      setNewMessage('');
    } catch (error) {
      console.error('Error sending message:', error);
      setError('Failed to send message');
    }
  };

  const handleInvite = async (e) => {
    e.preventDefault();
    if (!inviteEmail.trim()) return;

    try {
      await axios.post('/auth/invite', { email: inviteEmail.trim() });
      setShowInviteModal(false);
      setInviteEmail('');
      setError(null);
    } catch (error) {
      console.error('Error sending invite:', error);
      setError('Failed to send invite. Please try again.');
    }
  };

  return (
    <div className="chat-container">
      <div className="users-sidebar">
        <div className="sidebar-header">
          <h2>Users</h2>
          <button 
            className="invite-btn"
            onClick={() => setShowInviteModal(true)}
          >
            Invite
          </button>
        </div>
        <div className="users-list">
          {users.length > 0 ? (
            users.map(user => (
              <div
                key={user._id}
                className={`user-item ${selectedUser?._id === user._id ? 'selected' : ''}`}
                onClick={() => setSelectedUser(user)}
              >
                <div className="user-avatar">
                  {user.profilePicture ? (
                    <img src={user.profilePicture} alt={user.firstName} />
                  ) : (
                    <div className="avatar-placeholder">
                      {user.firstName[0]}
                    </div>
                  )}
                </div>
                <div className="user-info">
                  <h3>{user.firstName} {user.lastName}</h3>
                  <p>{user.headline}</p>
                </div>
              </div>
            ))
          ) : (
            <div className="no-users-message">
              <p>No users found</p>
              <p>Invite your LinkedIn connections to start chatting!</p>
              <button 
                className="invite-btn"
                onClick={() => setShowInviteModal(true)}
              >
                Invite Users
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="chat-main">
        {selectedUser ? (
          <>
            <div className="chat-header">
              <h2>Chat with {selectedUser.firstName} {selectedUser.lastName}</h2>
            </div>
            <div className="messages-container">
              {messages.map((message, index) => (
                <div
                  key={index}
                  className={`message ${message.senderId === selectedUser._id ? 'received' : 'sent'}`}
                >
                  <div className="message-content">{message.content}</div>
                  <div className="message-time">
                    {new Date(message.createdAt).toLocaleTimeString()}
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>
            <form onSubmit={handleSendMessage} className="message-input">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type a message..."
              />
              <button type="submit">Send</button>
            </form>
          </>
        ) : (
          <div className="no-chat-selected">
            <p>Select a user to start chatting</p>
            {users.length === 0 && (
              <button 
                className="invite-btn"
                onClick={() => setShowInviteModal(true)}
              >
                Invite Users
              </button>
            )}
          </div>
        )}
      </div>

      {showInviteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Invite to Chat</h2>
            <p>Enter the email address of the LinkedIn user you want to invite:</p>
            <form onSubmit={handleInvite}>
              <input
                type="email"
                value={inviteEmail}
                onChange={(e) => setInviteEmail(e.target.value)}
                placeholder="Enter email address"
                required
              />
              <div className="modal-actions">
                <button type="button" onClick={() => setShowInviteModal(false)}>
                  Cancel
                </button>
                <button type="submit">Send Invite</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError(null)}>Ã—</button>
        </div>
      )}
    </div>
  );
}

export default Chat;
